function mask1(uint8 bitPos) external pure returns(uint256 mask) {
    mask = (1 << bitPos) - 1 + (1 << bitPos);
}
    
function mask2(uint8 bitPos) external pure returns(uint256 mask) {
    mask = (1 << (bitPos+1)) - 1;
}
